```python
def solve_sudoku(board):
    def backtrack(row, col):
        if row == len(board) or col == len(board[0]):
            return True  # If we've reached the end of the board

        if board[row][col] != '.':
            return backtrack(row + 1, 0)  # Skip already filled cells

        for num in range(1, 10):
            if is_valid(board, row, col, num):
                board[row][col] = str(num)
                if backtrack(row + 1, col):  # Recursively call solve_sudoku on the next cell
                    return True
                board[row][col] = '.'

        return False

    def is_valid(board, row, col, num):
        for i in range(len(board)):
            if board[i][col] == num or \
               (row - i >= 0 and board[row - i][col] == num) or \
               (col - i >= 0 and board[row][col - i] == num):
                return False

        start_row, start_col = row - row % 3, col - col % 3
        for i in range(start_row, start_row + 3):
            for j in range(start_col, start_col + 3):
                if board[i][j] == num:
                    return False
        return True

    solve_sudoku(board)
